function fadeInOnScroll({triggerElement,fadeInElements=[],repeat=!1,delay=200,delayIncrement=200}){let hasAnimationRan=!1;if(!repeat){if(hasAnimationRan){return}}
let currentScrollTop=document.body.scrollTop;let triggerElementStyle=getComputedStyle(triggerElement);let triggerElementScrollTop=triggerElement.getBoundingClientRect().top-parseInt(triggerElementStyle.marginTop);if(triggerElementScrollTop<currentScrollTop){for(let i=0;i<fadeInElements.length;i++){setTimeout(function(){fadeInElements[i].classList.add("fadeIn");fadeInElements[i].style.visibility="visible"},delay);delay+=delayIncrement}}
hasAnimationRan=!0}
